import * as React from "react";
import "./styles.css";
import styled from "styled-components";
import useSwipeEvent from "./useSwipeEvent";

const Square = styled.div `
  min-width: 300px;
  min-height: 300px;
  background-color: #ccc;
  margin: 100px;
`;

const {useEffect, useRef} = React;

export default function App() {
  const ctnRef = useRef(null);

  // useSwipeEvent(() => {   console.log("Yahuurr1"); }, ctnRef.current);

  useEffect(() => {
    console.log("Yahuurr", ctnRef.current);
    useSwipeEvent(() => {
      console.log("Yahuurr1");
    }, ctnRef.current);
  }, []);

  console.log('DDDD', ctnRef.current);

  return (
    <div className="App">
      <Square ref={ctnRef}/>
      <h1>Hey</h1>
    </div>
  );
}
